import 'package:chatgpt/models/Models.dart';import 'package:chatgpt/services/api_service.dart';import 'package:flutter/material.dart';class ModelsProvider with ChangeNotifier {  String currentModel = "babbage";  String get getCurrentModel {    return currentModel;  }  void setCurrentModel(String currentModel) {    this.currentModel = currentModel;    notifyListeners();  }  List<ModelsModel> modelList = [];  List<ModelsModel> get getModelsList {    return modelList;  }  Future<List<ModelsModel>> getAllModels() async{    modelList = await ApiService.getModels();    return modelList;  }}